import React from "react";
import './Sidebar.css';
import Accordion from "./Accordion";

function Sidebar({ page }) {
    const getItems = () => {
        if (page === "math") {
            return sidebars[0];
        } else if (page === "page2") {
            return [
                {title: "Chapter 3: Linear Functions", content:"This is the content for chapter 3."},
                {title: "Chapter 4: Exponential Functions", content:"This is the content for chapter 4."}
            ];
        } else {
            return [
                {title: "Default Chapter", content:"This is the default content."}
            ];
        }
    };

    return (
        <div className="sidebar">
            <div className="p-4">
                <h1>Analysis & Approaches</h1>
                <h2>Units</h2>
            </div>
            <Accordion items={getItems()}/>
        </div>
    );
}

export default Sidebar;

