import React from "react";
import './Sidebar.css';
import Accordion from "./Accordion";

function Sidebar({ page }) {
    const getItems = () => {
        if (page === "math") {
            return [
                {title: "Chapter 1: Functions", content:"This is the content for chapter 1."},
                {title: "Chapter 2: Quadratic Functions", content:"This is the content for chapter 2."}
            ];
        } else if (page === "page2") {
            return [
                {title: "Chapter 3: Linear Functions", content:"This is the content for chapter 3."},
                {title: "Chapter 4: Exponential Functions", content:"This is the content for chapter 4."}
            ];
        } else {
            return [
                {title: "Default Chapter", content:"This is the default content."}
            ];
        }
    };

    return (
        <div className="sidebar">
            <Accordion items={getItems()}/>
        </div>
    );
}

export default Sidebar;
