import React, { useState } from "react";
import './Sidebar.css';
import Accordion from "./Accordion";

function Sidebar({ page }) {
    const [collapsed, setCollapsed] = useState(false);

    const getItems = () => {
        if (page === "math/aa") {
            return sidebars[0];
        } else if (page === "math/ai") {
            return sidebars[1];
        } else {
            return [
                {title: "Default Chapter", content:"This is the default content."}
            ];
        }
    };

    return (
        <div className={`sidebar-container ${collapsed ? "collapsed" : ""}`}>
            <div className={`sidebar`}>
                <div className="sidebar-content">
                    <div className="p-4">
                        <h1>Analysis & Approaches</h1>
                        <h2>Units</h2>
                    </div>
                    <Accordion items={getItems()}/>
                </div>
            </div>
            <button
                className={`sidebar-toggle ${collapsed ? "collapsed" : ""}`}
                onClick={() => setCollapsed(!collapsed)}
            >
            </button>
        </div>
    );
}

export default Sidebar;

// In Sidebar.js (or wherever your sidebar data is defined)
const sidebars = [
    [ // AA [0]
        {
            title: "Chapter 1: Functions",
            content: (
                <div className="flex flex-column gap-2">
                <a href="/chapter1/lesson1">Lesson 1</a>
                <a href="/chapter1/lesson2">Lesson 2</a>
                <a href="/chapter1/lesson3">Lesson 3</a>
            </div>
            )
        },
        {
            title: "Chapter 2: Quadratic Functions",
            content: (
            <div className="flex flex-column gap-2">
                <a href="/chapter2/overview">Overview</a>
                <a href="/chapter2/examples">Examples</a>
                <a href="/chapter2/exercises">Exercises</a>
            </div>
            )
        },
        // ... other chapters
    ],
    [ // AI [1]
        {
            title: "Chapter 1: Barbosa",
            content: (
                <div className="chapter-links">
                <a href="/barbosa/info">Info</a>
                <a href="/barbosa/details">Details</a>
                </div>
            )
        }
    ]
];
