import React, { useState, useRef, useEffect } from "react";
import "./AccordionButton.css";

function AccordionButton({ title, content }) {
  const [isOpen, setIsOpen] = useState(false);
  const contentRef = useRef(null);
  const [maxHeight, setMaxHeight] = useState("0px");

  const toggleContent = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    if (contentRef.current) {
      // When open, set maxHeight to the scrollHeight of the content,
      // otherwise set it to 0 to hide the content.
      setMaxHeight(isOpen ? `${contentRef.current.scrollHeight}px` : "0px");
    }
  }, [isOpen, content]);

  return (
    <div className="item">
      <button className="title" onClick={toggleContent}>
        <h2>{title}</h2>
      </button>
      <div
        ref={contentRef}
        className={`accordion-content ${isOpen ? "open" : ""}`}
        style={{ maxHeight }}
      >
        <div className="content-wrapper">{content}</div>
      </div>
    </div>
  );
}

export default AccordionButton;
