import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Button } from './Button';

function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(false)
        } else {
            setButton(true)
        }
    }

    window.addEventListener('resize', showButton);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo">
                        TRVL <i className="fab fa-typo3"></i>
                    </Link> 
                    <div className="menu-icon" onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                        <li className="nav-item">
                            <Link to="/" className="nav-links" onClick="{closeMobileMenu}">
                                Home
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/subjects" className="nav-links" onClick="{closeMobileMenu}">
                                Subjects
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/resources" className="nav-links" onClick="{closeMobileMenu}">
                                Resources
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/feedback" className="nav-links" onClick="{closeMobileMenu}">
                                Feedback
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/about-us" className="nav-links" onClick="{closeMobileMenu}">
                                About Us
                            </Link>
                        </li>
                        <li className="nav-item">
                            <Link to="/submit" className="nav-links" onClick="{closeMobileMenu}">
                                Submit
                            </Link>
                        </li>
                    </ul>
                    {button && <Button buttonStyle='btn--outline'>SUBJECTS</Button>}
                    {button && <Button buttonStyle='btn--outline'>RESOURCES</Button>}
                    {button && <Button buttonStyle='btn--outline'>FEEDBACK</Button>}
                    {button && <Button buttonStyle='btn--outline'>ABOUT US</Button>}
                    {button && <Button buttonStyle='btn--outline'>SUBMIT</Button>}
                </div>
            </nav>
        </>
    )
}

export default Navbar
