// Mathematics.js
import React, { useState } from 'react';
import { Routes, Route, Outlet } from 'react-router-dom';
import Sidebar from '../components/Sidebar';
import ContentWrapper from '../components/ContentWrapper';
import Video from '../components/Video';
import BackNext from '../components/BackNext';
import { Document, Page } from 'react-pdf';
import pdf13 from '../files/math/aa/13.pdf';

import { pdfjs } from 'react-pdf';

pdfjs.GlobalWorkerOptions.workerSrc = new URL(
  'pdfjs-dist/build/pdf.worker.min.mjs',
  import.meta.url,
).toString();

const Mathematics = () => {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
    setPageNumber(1);
  }

  function changePage(offset) {
    setPageNumber(prevPageNumber => prevPageNumber + offset);
  }

  function previousPage() {
    changePage(-1);
  }

  function nextPage() {
    changePage(1);
  }

  return (
    <Routes>
      <Route
        path="aa/*"
        element={
          <div className="flex flex-grow self-stretch">
            <Sidebar page="math/aa" />
            <Outlet />
          </div>
        }
      >
        <Route element={<ContentWrapper />}>
          <Route
            path="1-1"
            element={
              <>
                <h1 className="!mb-5">
                  1.1: Numbers – rounding – scientific form
                </h1>
                <Video url="https://www.youtube.com/embed/fxdYta_Olv0" />
                {/* Add more content for 1-1 here */}
                <BackNext next="1-2" />
              </>
            }
          />

          <Route
            path="1-2"
            element={
              <>
                <h1 className="!mb-5">
                  1.2: Sequences in general - Series
                </h1>
                <Video url="https://www.youtube.com/embed/Tj89FA-d0f8" />
                <Document
                  className="border border-black"
                  file={pdf13}
                  onLoadSuccess={onDocumentLoadSuccess}
                >
                  <Page pageNumber={pageNumber} />
                </Document>
                <div className="mt-4">
                  <p>
                    Page {pageNumber || (numPages ? 1 : '--')} of{' '}
                    {numPages || '--'}
                  </p>
                  <button
                    type="button"
                    disabled={pageNumber <= 1}
                    onClick={previousPage}
                  >
                    Previous
                  </button>
                  <button
                    type="button"
                    disabled={pageNumber >= numPages}
                    onClick={nextPage}
                    className="ml-2"
                  >
                    Next
                  </button>
                </div>
                <BackNext back="1-1" next="1-3" />
              </>
            }
          />

          <Route
            path="1-3"
            element={
              <>
                <h1 className="!mb-5">1.3: Arithmetic sequences</h1>
                <BackNext back="1-2" next="1-4" />
              </>
            }
          />

          <Route
            path="1-4"
            element={
              <>
                <h1 className="!mb-5">1.4: Geometric sequences</h1>
                <BackNext back="1-3" next="1-5" />
              </>
            }
          />

          <Route
            path="1-5"
            element={
              <>
                <h1 className="!mb-5">
                  1.5: Applications of G.S. – Percentage growth
                </h1>
                <BackNext back="1-4" next="1-6" />
              </>
            }
          />

          <Route
            path="1-6"
            element={
              <>
                <h1 className="!mb-5">
                  1.6: The Binomial Theorem – <i>(a+b)<sup>n</sup></i>
                </h1>
                <BackNext back="1-5" next="1-7" />
              </>
            }
          />

          <Route
            path="1-7"
            element={
              <>
                <h1 className="!mb-5">1.7: Deductive proof</h1>
                <BackNext back="1-6" next="1-8" />
              </>
            }
          />

          <Route
            path="1-8"
            element={
              <>
                <h1 className="!mb-5">1.8: Methods of proof</h1>
                <BackNext back="1-7" next="1-9" />
              </>
            }
          />

          <Route
            path="1-9"
            element={
              <>
                <h1 className="!mb-5">1.9: Mathematical induction</h1>
                <BackNext back="1-8" next="1-10" />
              </>
            }
          />

          <Route
            path="1-10"
            element={
              <>
                <h1 className="!mb-5">
                  1.10: Systems of linear equations
                </h1>
                <BackNext back="1-9" next="1-11" />
              </>
            }
          />

          <Route
            path="1-11"
            element={
              <>
                <h1 className="!mb-5">
